mapper.update:
UPDATE product
prod_name=#{prodName:VARCHAR}, prod_detail=#{prodDetail:VARCHAR}, manufacture_day=#{manuDate:DATE}, price=#{price:NUMERIC}, image_file=#{fileName:VARCHAR}
WHERE prod_no = #{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT p.prod_no, p.prod_name, p.price, p.reg_date, p.prod_detail, t.tran_status_code FROM product p, transaction t
t.prod_no(+)= p.prod_no
AND p.prod_no = #{searchKeyword}
AND prod_name LIKE '%${searchKeyword}%'
AND price = #{searchKeyword}
ORDER BY p.prod_no ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*) FROM (SELECT p.prod_no, p.prod_name, p.price, p.reg_date, p.prod_detail, t.tran_status_code FROM product p, transaction t
t.prod_no(+)= p.prod_no
AND p.prod_no = #{searchKeyword}+10000
AND prod_name = #{searchKeyword}
AND price = #{searchKeyword}
ORDER BY p.prod_no ) countTable
